mapper.select:
SELECT USER_NO, EMAIL, PW, NAME, GENDER, MOBILE, EVENT_AGREE, SIGNUP_KIND, PW_MODIFY_DT, SIGNUP_DT
  FROM USER_T

  EMAIL = #{email}
  AND PW = #{pw}

mapper.insert:
INSERT INTO ACCESS_HISTORY_T (
    ACCESS_HISTORY_NO
  , EMAIL
  , IP
  , USER_AGENT
  , SESSION_ID
  , SIGNIN_DT
  , SIGNOUT_DT
) VALUES (
  ACCESS_HISTORY_SEQ.NEXTVAL
  , #{email}
  , #{ip}
  , #{userAgent}
  , #{sessionId}
  , CURRENT_DATE
  , NULL
)

mapper.update:
UPDATE ACCESS_HISTORY_T
   SET SIGNOUT_DT = CURRENT_DATE
 WHERE SESSION_ID = #{sessionId}

mapper.insert:
INSERT INTO USER_T (
    USER_NO
  , EMAIL
  , PW
  , NAME
  , GENDER
  , MOBILE
  , EVENT_AGREE
  , SIGNUP_KIND
  , PW_MODIFY_DT
  , SIGNUP_DT
) VALUES (
    USER_SEQ.NEXTVAL
  , #{email}
  , #{pw}
  , #{name}
  , #{gender}
  , #{mobile}
  , #{eventAgree}
  , 0
  , CURRENT_DATE
  , CURRENT_DATE
)
